import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
    rowContainer: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: 5
    },
    titleContainer: {
      flex: 1,
      padding: 5,
      justifyContent: 'center',
    },
    image: {
      height: 75,
      aspectRatio: 16/9,
      resizeMode: 'cover',
      borderRadius: 3,
    },
    title: {
      color: 'white',
      fontSize: 18,
    },
    duration:{
      color: 'darkgrey',
      fontSize: 18,
    },
    plot: {
      color: 'darkgrey',
    }
  });

  export default styles;
